<?php/** * BiteFight * Fixed by: ExtremsX * Versão: 1.1 * Revisão: 2013/01/08 */require 'include/config.php';require 'include/tpl/top.php';$user = user_information( $_SESSION['id'] );$clan = clan_information( $user );if ( !$clan ) {    echo "<script type=\"text/javascript\">window.location = 'clan.php'; </script>";    exit;}if ( $clan['user_id'] != $user['id'] ) {    echo "<script type=\"text/javascript\">window.location = 'clan_information.php'; </script>";    exit;}if ( isset( $_POST['tag'] ) ) {    if ( strlen( $_POST['tag'] ) < 2 || strlen( $_POST['tag'] ) > 8 )        $msg = '<tr><td colspan="2" align="center" style="color:yellow">A TAG deve conter entre 2 e 8 caracteres.</td></tr>';    else if ( clan_tag_in_use( $_POST['tag'] ) )        $msg = '<tr><td colspan="2" align="center" style="color:yellow">A TAG já está em uso.</td></tr>';    else        clan_tag( $clan, $_POST['tag'] );}?><div>    <h1>Clã | <a href="clan_information.php" >voltar</a></h1>    <h2>Mudar a TAG do Clã</h2>    <p>        <table width="100%">            <form action="clan_tag.php" method="post">                <?php if ( isset( $msg ) ) echo $msg; ?>                <tr>                    <th>TAG do Clã:</th>                    <th><?php echo $clan['tag']; ?></th>                </tr>                <tr>                    <th>Nova tag do clã:</th>                    <th><input type="text" name="tag" size="20" maxlength="8" value="<?php if ( isset( $_POST['tag'] ) ) echo $_POST['tag']; ?>"></th>                </tr>                <tr>                    <th colspan="2"><input type="submit" value="salvar"></th>                </tr>            </form>        </table>    </p></div><?php require 'include/tpl/footer.php'; ?>